import React, { useState } from "react";
import PropTypes from "prop-types";

const defaultLinks = [
  { id: 1, label: "About Natous", prefix: "01", href: "#" },
  { id: 2, label: "Your benefits", prefix: "02", href: "#" },
  { id: 3, label: "Popular tours", prefix: "03", href: "#" },
  { id: 4, label: "Stories", prefix: "04", href: "#" },
  { id: 5, label: "Book now", prefix: "05", href: "#" },
];

const Navigation = ({ links = defaultLinks }) => {
  const [open, setOpen] = useState(false);

  const toggleNav = () => setOpen((o) => !o);
  const closeNav = () => setOpen(false);

  return (
    <div className={`navigation${open ? " navigation--open" : ""}`}>
      {/* Hidden checkbox equivalent for CSS targeting if needed */}
      <input
        type="checkbox"
        className="navigation__checkbox"
        id="navi-toggle"
        checked={open}
        onChange={toggleNav}
        aria-label="Toggle navigation"
      />

      <label
        htmlFor="navi-toggle"
        className="navigation__button"
        onClick={toggleNav}
        aria-expanded={open}
        aria-controls="navigation-menu"
        role="button"
      >
        <span className="navigation__icon">&nbsp;</span>
      </label>

      <div className="navigation__background" aria-hidden="true">
        &nbsp;
      </div>

      <nav className="navigation__nav" id="navigation-menu">
        <ul className="navigation__list">
          {links.map((link) => (
            <li className="navigation__item" key={link.id}>
              <a
                href={link.href}
                className="navigation__link"
                onClick={closeNav}
              >
                <span>{link.prefix}</span>
                {link.label}
              </a>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
};

Navigation.propTypes = {
  links: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.any.isRequired,
      label: PropTypes.string.isRequired,
      prefix: PropTypes.string,
      href: PropTypes.string,
    })
  ),
};

export default Navigation;
