import React, { useState } from "react";

const defaultLinks = [
  { id: 1, label: "About Natous", prefix: "01", href: "#" },
  { id: 2, label: "Your benefits", prefix: "02", href: "#" },
  { id: 3, label: "Popular tours", prefix: "03", href: "#" },
  { id: 4, label: "Stories", prefix: "04", href: "#" },
  { id: 5, label: "Book now", prefix: "05", href: "#" },
];

const Navigation = ({ links = defaultLinks }) => {
  const [open, setOpen] = useState(false);
  return (
    <div
      className={`navigation ${open ? "is-open" : ""}`}
      style={{ backgroundColor: "green" }}
    >
      <button
        aria-label="Toggle navigation"
        className="navigation__button"
        onClick={() => setOpen((o) => !o)}
      >
        <span className="navigation__icon" />
      </button>

      <div className="navigation__background" />

      <nav className="navigation__nav" aria-label="Main navigation">
        <ul className="navigation__list">
          {links.map((link) => (
            <li className="navigation__item" key={link.id}>
              <a
                href={link.href}
                className="navigation__link"
                onClick={() => setOpen(false)}
              >
                <span>{link.prefix}</span> {link.label}
              </a>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
};

export default Navigation;
